    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        
        Boolean guardar = true;
        String mensaje = "";
        String nombre = txtNombre.getText();
        int edad = Integer.parseInt(spnEdad.getValue().toString());
        String sexo = cmbSexo.getSelectedItem().toString();
        if (nombre.length() == 0) {
            guardar = false;
            mensaje += "Escriba el nombre, por favor\n";
        }
        if (edad == 0) {
            guardar = false;
            mensaje += "Escriba la edad, por favor\n";
        }
        if (guardar) {
            ControllerDatos control = new ControllerDatos();
            if (sexo.equals("Fememino")) {
                sexo = "F";
            }
            if (sexo.equals("Masculino")) {
                sexo = "M";
            }
            control.insertar(nombre, edad, sexo);
            txtNombre.setText("");
            spnEdad.setValue(0);
            cmbSexo.setSelectedIndex(0);
        } else {
            JOptionPane.showMessageDialog(null, mensaje);
        }
    }//GEN-LAST:event_btnGuardarActionPerformed

